function init() {
    ymaps.geocode(addr, {
        results: 1
    }).then(function(e) {
        var t = e.geoObjects.get(0).geometry.getCoordinates(), a = new ymaps.Placemark(t);
        (yaMap = new ymaps.Map("map", {
            center: t,
            zoom: 15,
            controls: [ "zoomControl", "fullscreenControl" ]
        })).geoObjects.add(a);
    });
}

if ("undefined" != typeof ymaps) {
    ymaps.ready(init);
    var yaMap;
}

$(document).ready(function() {
    $(".slider-hero").owlCarousel({
        loop: !1,
        nav: !0,
        navText: [ '<img src="img/arrow-left.svg" alt="Влево">', '<img src="img/arrow-right.svg" alt="Вправо">' ],
        items: 1,
        animateOut: "fadeOut",
        mouseDrag: !1
    }), $(".product-gallary-slider").owlCarousel({
        loop: !0,
        nav: !0,
        navText: [ '<img src="img/arrow-left.png" alt="Влево">', '<img src="img/arrow-right.png" alt="Вправо">' ],
        items: 3,
        center: !0,
        margin: 10
    }), $(".product-gallary-slider__item").click(function() {
        $(".product-gallary-full__item").removeClass("active"), document.getElementById($(this).attr("data-pic")).classList.add("active");
    }), $(".product-gallary-slider").on("changed.owl.carousel", function(e) {
        setTimeout(function() {
            $(".product-gallary-full__item").removeClass("active"), document.getElementById($(e.target).find(".owl-item.center").children().attr("data-pic")).classList.add("active");
        }, 0);
    });
    var e = window.pageYOffset, t = !1;
    window.addEventListener("scroll", function() {
        (e = window.pageYOffset) > document.documentElement.clientHeight && !t ? (t = !0, 
        document.querySelector(".up").classList.add("view")) : e <= document.documentElement.clientHeight && t && (t = !1, 
        document.querySelector(".up").classList.remove("view"));
    }), document.querySelector(".up").addEventListener("click", function() {
        $("html, body").animate({
            scrollTop: 0
        }, "slow");
    });
});